using UnityEngine; using UnityEngine.SceneManagement; using UnityEngine.UI;  public class Player : MonoBehaviour {          private Animator animator; 

    public float forceX =2; 	public float maxX =3;  	Rigidbody2D rb;  	public int onFloor = 1;  	void Start () {         animator = GetComponent<Animator>(); 		rb = GetComponent<Rigidbody2D> ();         	              }   	void Update () { 		float valX = 0;  		if (Input.GetKeyDown (KeyCode.Space)) {             animator.SetTrigger("Jump"); 			if(onFloor == 1){ 				rb.AddForce (new Vector2 (0, 300)); 				onFloor = 0; 			} 		}  		if(Input.GetKey (KeyCode.RightArrow)){             animator.SetTrigger("Walk_R");          			rb.AddForce (new Vector2 (50,0)); 		}   		if(Input.GetKey (KeyCode.LeftArrow)){             animator.SetTrigger("Walk_L");           			rb.AddForce (new Vector2 (-50,0)); 		}   		if (valX == 0) { 			Vector2 freno = rb.velocity; 			freno.x -= rb.velocity.x * 0.4f; 			rb.velocity = freno; 		}   	}        	void OnTriggerEnter2D(Collider2D col){ 		  		if (col.tag.Equals ("Floor")) {  			onFloor = 1; 		}    	}  	void OnCollisionEnter2D(Collision2D colider){ 		onFloor = 1; 	}                          }  