using UnityEngine; using UnityEngine.SceneManagement; using UnityEngine.UI;  public class Player : MonoBehaviour { 	public int hp=100;   	public float forceX =2; 	public float maxX =3; 	Rigidbody2D rb;  	public int onFloor = 1;  	void Start () { 		rb = GetComponent<Rigidbody2D> ();  	}   	void Update () {   		float valX = 0;  		if (Input.GetKeyDown (KeyCode.Space)) { 			if(onFloor == 1){ 				rb.AddForce (new Vector2 (0, 300)); 				onFloor = 0; 			} 		}  		if(Input.GetKey (KeyCode.RightArrow)){ 			rb.AddForce (new Vector2 (50,0)); 		}   		if(Input.GetKey (KeyCode.LeftArrow)){ 			rb.AddForce (new Vector2 (-50,0)); 		}   		if (valX == 0) { 			Vector2 freno = rb.velocity; 			freno.x -= rb.velocity.x * 0.4f; 			rb.velocity = freno; 		}  	} 	void OnTriggerEnter2D(Collider2D col){ 		if (col.tag.Equals ("Choque")) {  			GameController.score--;//  			GameObject txtObj = GameObject.Find ("scoreText");// busca el scoreText del canvas en unity 			Text txt= txtObj.GetComponent<Text>(); 			txt.text = "" + GameController.score;  			if(GameController.score < 1){ 				Destroy (gameObject); 				GameObject gcObj = GameObject.FindGameObjectWithTag ("GameController"); 				GameController gc = gcObj.GetComponent<GameController> (); 				gc.lose (); 			}  		}  		if (col.tag.Equals ("choquelevel2")) {  		 			GameObject txtObj = GameObject.Find ("scoreText");// busca el scoreText del canvas en unity 			Text txt= txtObj.GetComponent<Text>(); 		  			  		}   		if (col.tag.Equals ("Floor")) {  			onFloor = 1; 		}    	}  	void OnCollisionEnter2D(Collision2D colider){ 		onFloor = 1; 	} } 